// Function to get products - reads from JSON if available, otherwise uses static data
// This allows the admin panel to update prices dynamically
// Note: This function dynamically imports the storage module to avoid bundling issues
export async function getAllProducts(): Promise<ProductCardData[]> {
  try {
    // Dynamically import to avoid bundling server-only code in client components
    const { getProducts: getProductsFromStorage } = await import('@/lib/products-storage');
    const products = await getProductsFromStorage();
    return products;
  } catch (error) {
    // If JSON file doesn't exist or there's an error, fall back to static data
    console.warn('Could not load products from JSON, using static data:', error);
    return ALL_PRODUCTS;
  }
}

// For backwards compatibility and static generation, keep ALL_PRODUCTS export
export function getAllProductsSync(): ProductCardData[] {
  // For static generation, we'll use the static data
  // Dynamic pages should use getAllProducts() instead
  return ALL_PRODUCTS;
}